import React from 'react';

const Navigation = ({ theme, toolTheme, onThemeToggle, onToolThemeChange }) => {
  return (
    <nav className="navigation">
      <div className="nav-left">
        <div className="logo">
          <span className="logo-text">Project: Hampton</span>
          <span className="version">v0.0.3</span>
        </div>
      </div>

      <div className="nav-center">
        <div className="tool-selector">
          <button 
            className={`tool-btn ${toolTheme === 'claude' ? 'active' : ''}`}
            onClick={() => onToolThemeChange('claude')}
          >
            Claude Code
          </button>
          <button 
            className={`tool-btn ${toolTheme === 'puppy' ? 'active' : ''}`}
            onClick={() => onToolThemeChange('puppy')}
          >
            Code Puppy
          </button>
        </div>
      </div>

      <div className="nav-right">
        <label className="theme-toggle">
          <input 
            type="checkbox" 
            checked={theme === 'dark'}
            onChange={onThemeToggle}
          />
          <span className="theme-slider">
            <span className="theme-icon">{theme === 'dark' ? 'üåô' : '‚òÄÔ∏è'}</span>
          </span>
        </label>
      </div>
    </nav>
  );
};

export default Navigation;
